entryCriteria.formula:
OR( AND( OR( RecordType.DeveloperName = "TelesalesOrder", RecordType.DeveloperName = "ECommerceOrder", RecordType.DeveloperName = "EDIOrder", RecordType.DeveloperName = "FieldDirectOrder" ), OR( AND( OR( ISBLANK(Account.ParentId), AND(NOT(ISBLANK(Account.ParentId)), NOT(Account.Parent.DefaultInvoiceBillingAddress__c)) ), TotalAmount > Account.AvailableCredit__c ), AND( NOT(ISBLANK(Account.ParentId)), Account.Parent.DefaultInvoiceBillingAddress__c, TotalAmount > Account.Parent.AvailableCustomerCredit__c ) ) ),
AND( OR( RecordType.DeveloperName = "ECommerceOrder", RecordType.DeveloperName = "EDIOrder", RecordType.DeveloperName = "FieldDirectOrder" ), OR( AND( OR( ISBLANK(Account.ParentId), AND(NOT(ISBLANK(Account.ParentId)), NOT(Account.Parent.DefaultInvoiceBillingAddress__c)) ), ISPICKVAL(Account.CreditStatus__c, 'On Hold') ), AND( NOT(ISBLANK(Account.ParentId)), Account.Parent.DefaultInvoiceBillingAddress__c, ISPICKVAL(Account.Parent.CreditStatus__c, 'On Hold') )
) ) )
